import React, {useState, useEffect} from 'react';
import './App.css';
import { CSVLink } from "react-csv";
import Axios from "axios";

function timeConverter(UNIX_timestamp){
  var a = new Date(UNIX_timestamp * 1000);
  var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
  var year = a.getFullYear();
  var month = months[a.getMonth()];
  var date = a.getDate();
  var hour = a.getHours();
  var min = a.getMinutes();
  var sec = a.getSeconds();
  var time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
  return time;
}

function App() {
  const [timeLastUpdate, setTimeLastUpdate] = useState(0);
  const [topScoresList, setTopScoresList] = useState([]);

  // function updateReport(list) {
  //   state.setCsvReport(headersCsv.concat(list));
  // };


  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
        setTopScoresList(response.data);
        // updateReport(response.data);
        console.log("data fetched")
    })
    const interval = setInterval(() => {
      console.log("getting data pi pi pu pu");
      Axios.get('http://localhost:3001/api/get').then((response) => {
        setTopScoresList(response.data);
        console.log("data fetched")
      })
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <h1> Top Scores </h1>
      {topScoresList.map((elem) => {
        return (
          <h2>
            <img src={elem.profile_image}></img> {elem.nickname}  
            <h2>
                Score: {elem.value}
              <h6> 
                generated time: {elem.timestamp.toDateString()}
              </h6>
            </h2> 
          </h2>
        );
      })}
      <CSVLink data={topScoresList} >Download report</CSVLink>
    </div>
  );
}

export default App;
