import React, {useState, useEffect} from 'react';
import './App.css';
import { CSVLink } from "react-csv";
import Axios from "axios";

const headersCsv = ["Nickname","Profile Image Link", "Score"];

function App() {
  const [timeLastUpdate, setTimeLastUpdate] = useState(0);
  const [topScoresList, setTopScoresList] = useState([]);
  const [csvReport, setCsvReport] = useState([headersCsv]);

  function updateReport(list) {
    setCsvReport(headersCsv.concat(list));
  };


  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
        setTopScoresList(response.data);
        updateReport(response.data);
        console.log("data fetched")
    })
    const interval = setInterval(() => {
      console.log("getting data pi pi pu pu");
      Axios.get('http://localhost:3001/api/get').then((response) => {
        setTopScoresList(response.data);
        updateReport(response.data);
        console.log("data fetched")
      })
    }, 10000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="App">
      <h1> Top Scores </h1>
      {topScoresList.map((elem) => {
        return (
          <h2>
            <img src={elem.profile_image}></img> {elem.nickname}  <h2 className="score">Score: {elem.value}</h2> 
          </h2>
        );
      })}
      <CSVLink data={csvReport} >Download report</CSVLink>
    </div>
  );
}

export default App;
